import React, { Component } from 'react';
import * as d3 from 'd3';
import { render } from '@testing-library/react';

class Chart2 extends Component {
        constructor(props){
        super(props)
        this.chartRef = React.createRef();
        this.sliderRef = React.createRef();
    }

    componentDidMount () {

        const data = [12,36,55,25,35,10,40];

        const width = 1400;
        const height = 600;

        const niceFormat = d3.utcFormat("%B %d, %Y");
        const dataFormat = d3.utcFormat("%m/%d/%Y");
        const dataParse = d3.utcParse("%m/%d/%Y");

        const startDate = dataParse("01/21/2020");
        const millisecondsPerDay = 24 * 60 * 60 * 1000;
        const valRange = [0, 5500000, 8500000];
        const rateType = "Confirmed";
        //const {data, chartType, slider} = this.props;

        const accessToSliderRef = d3.select(this.sliderRef.current)
            .append("input")
            .attr("id", "slider")
            .attr("type", "range")
            .attr("min", 0)
            .attr("step", 1)
            .attr("value", 0)
            .attr("class", "slider");
