import React, {memo, useEffect, useState} from "react";
import {
    ZoomableGroup,
    ComposableMap,
    Geographies,
    Geography
} from "react-simple-maps";
import Axios from 'axios';

const geoUrl =   "/custom.geo.json";
const StartDate = "03/24/2020";

const USAChart = (props) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);


    useEffect(() => {
        Axios.get("http://localhost:5000/USA").then((response) =>{
            setData(response.data.filter(o => o.Updated === props.date));
            setLoading(false);
        });
    }, []);

    if (props.map === "USA"){
        return (
            <>
                <ComposableMap data-tip="" projection="geoAlbersUsa" width={800} height={600}>
                    <ZoomableGroup zoom={1}>>
                        <Geographies geography={geoUrl}>
                            {({ geographies }) =>
                                geographies.map(geo => (
                                    <Geography
                                        key={geo.rsmKey}
                                        geography={geo}
                                        onMouseEnter={() => {
                                            if(loading){
                                                props.setTooltipContent('Data is loading ' + props.date)
                                            }else{
                                                if (data){
                                                    props.setTooltipContent(`${geo.properties.name}</br>
                                                        Confirmed - ${data[0].Confirmed}</br>`);
                                                } else {
                                                    props.setTooltipContent(`${geo.properties.name}</br>
                                                        Confirmed - 0</br>`);
                                                }

                                            }
                                        }}
                                        onMouseLeave={() => {
                                            props.setTooltipContent("");
                                        }}
                                        style={{
                                            default: {
                                                fill: "#D6D6DA",
                                                outline: "none"
                                            },
                                            hover: {
                                                fill: "#F53",
                                                outline: "none"
                                            },
                                            pressed: {
                                                fill: "#E42",
                                                outline: "none"
                                            }
                                        }}
                                    />
                                ))
                            }
                        </Geographies>
                    </ZoomableGroup>
                </ComposableMap>
            </>
        );
    }else {
        return null;
    }
};

export default memo(USAChart);
