import React, { Component } from 'react';
import * as d3 from 'd3';
import { render } from '@testing-library/react';

class Chart2 extends Component {
        constructor(props){
        super(props)
        this.chartRef = React.createRef();
        this.sliderRef = React.createRef();
    }

    componentDidMount () {

        const data = [12,36,55,25,35,10,40];

        const w = 500;
        const h = 400;

        const niceFormat = d3.utcFormat("%B %d, %Y");
        const dataFormat = d3.utcFormat("%m/%d/%Y");
        const dataParse = d3.utcParse("%m/%d/%Y");

        const startDate = dataParse("01/21/2020");
        const millisecondsPerDay = 24 * 60 * 60 * 1000;
        const valRange = [0, 5500000, 8500000];
        const rateType = "Confirmed";
        //const {data, chartType, slider} = this.props;

        const accessToSliderRef = d3.select(this.sliderRef.current).on("input", function () {
            var date = new Date(+startDate + millisecondsPerDay * this.value);
            update(date);
        });

        const accessToChartRef = d3.select(this.chartRef.current)
            .append("svg")
            .attr("width",w)
            .attr("height",h)
            .style("background-color", "#cccccc")
            .style("padding", 10)
            .style("margin-left", 50);

        accessToChartRef.selectAll("rect")
            .data(data)
            .enter()
            .append("rect")
            .attr("x", (d,i) => i *70)
            .attr("y", (d,i) => h-10*d)
            .attr("width", 65)
            .attr("height", (d,i) => d*10)
            .attr("fill", (d,i) => d > 35 ? "tomato" : "yellow");
    }



    render() {
        return (
            <div>
                <div ref={this.chartRef}></div>
                <div id="slidecontainer">
                    <input id="slider" type="range" min="0" step="1" value="0" className="slider"/>
                    <span id="date"></span>
                </div>
            </div>
        )
    };

}

export default Chart2;
