import React, { Component } from 'react';
import * as d3 from 'd3';
import { render } from '@testing-library/react';

class Chart2 extends Component {
        constructor(props){
        super(props)
        this.chartRef = React.createRef();
    }

    componentDidMount () {
        data = this.props.data

        const width = 1400;
        const height = 600;
        const rateType = "Confirmed";

        const geoData = d3.json("custom.geo.json")

        const projection = d3
            .geoAlbersUsa()
            .translate([width / 2, height / 2])
            .scale([1280]);
        const path = d3.geoPath().projection(projection)

        const accessToChartRef = d3.select(this.chartRef.current)
            .append("SVG")

        let allStats = data[1].map(function (row, i) {
            return {
                Updated: dataParse(row.Updated),
                Country_Region: row.Country_Region,
                Confirmed: row.Confirmed,
                ConfirmedChange: row.ConfirmedChange,
                Deaths: row.Deaths,
                DeathsChange: row.DeathsChange,
            };
        });

        const stats = allStats.filter(function (row, i) {
            return +row.Updated === +date;
        });

        accessToChartRef
            .append("g")
            .attr("class", "states")
            .attr("class", "state-borders")
            .selectAll("path")
            .enter()
            .append("path")
            .attr("d", path);

        accessToChartRef.selectAll("path").attr("fill", function (d) {
            const metrics = getStateMetrics(stats, d.properties.name);
            if (metrics === undefined) return "#ddd";
            const rate = getStateMetrics(stats, d.properties.name)[rateType];
            return colormap(rate);
        });

        function getStateMetrics(stats, state_name) {
            for (var i = 0; i < stats.length; i++) {
                if (stats[i].Country_Region == state_name) {
                    return stats[i];
                }
            }
        }
    }





    render() {
        return (
            <div ref={this.chartRef}></div>
            /*<div>
                <div ref={this.chartRef}></div>
                <div id="slidecontainer">
                    <div ref={this.sliderRef}/>
                    <h3>{this.state.slider}</h3>
                    <span id="date"></span>
                </div>
            </div>*/
        )
    };

}

export default Chart2;


