import logo from './logo.svg';
import './App.css';
import React from "react";
import {useState} from "react";
const mysql = require('mysql');

function getData() {
    const table = [];

    const con = mysql.createConnection({
        host: 'localhost',
        user: 'root',
        password: 'password',
        database: 'sprint0'
    });

    con.query('SELECT * FROM testTable', (err, rows) => {
        if(err) throw err;
        rows.forEach( (row) => {
            const tableRow = {
                emplId: row.emplId,
                firstName: row.firstName,
                lastName: row.lastName,
                phoneNum: row.phoneNum,
                age: row.age
            }
            table.push(tableRow)
        })
    })

    return table
}


function App() {
    const [table, setTable] = useState([]);
  return (
    <div className="App">
      <div className="content">
          <h1>Hello World</h1>
      </div>
    </div>
  );
}

class Data extends React.Component {



    constructor(props) {
        super(props);
        this.state = getData();
    }

    render() {

        const tableRow = this.state.map((row) =>
            <tr>
                <td>{row.emplId}</td>
                <td>{row.firstName}</td>
                <td>{row.lastName}</td>
                <td>{row.phoneNum}</td>
                <td>{row.age}</td>
            </tr>
        );
        return (
            <table>{tableRow}</table>
        )
    }
}


export default Data;
